INSERT INTO `topics` (`id_topics`, `name_topics`, `description_topics`) VALUES
(1, 'PHP', 'Крутой язык boy'),
(3, '3D-моделирование', '3D-моделирование - это 52 52'),
(8, 'JavaScript-разработчик', 'JavaScript (JS) — это один из самых популярных и востребованных языков программирования, который используется для создания интерактивных и динамических веб-страниц. Он является основой фронтенд-разработки, но также активно применяется в бэкенде (с помощью Node.js), мобильной разработке (React Native) и даже в создании desktop-приложений (Electron). JavaScript позволяет оживить сайты, добавляя анимации, обработку событий, взаимодействие с пользователем и многое другое.'),
(12, 'Toп курсов и статей', 'Категория для вывода статей в горизонтальное слайд шоу на главной странице'),
(13, 'Веб-разработчик', 'Веб-разработчик — это специалист, который создает, поддерживает и улучшает веб-сайты и веб-приложения. В его задачи входит разработка как визуальной части (фронтенд), так и серверной логики (бэкенд), а также обеспечение корректной работы всех компонентов. Веб-разработчики работают с различными языками программирования, фреймворками и инструментами, чтобы создавать функциональные, удобные и современные digital-продукты.'),
(14, 'Python-разработчик', 'Python — это высокоуровневый язык программирования, который славится своей простотой, читаемостью и универсальностью. Он широко используется в различных областях: от веб-разработки и анализа данных до искусственного интеллекта и автоматизации. Благодаря понятному синтаксису и мощным библиотекам Python стал одним из самых популярных языков среди разработчиков, ученых и инженеров.');

